import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useRocketCredits } from "../context/RocketCreditsContext";

const Payment = () => {
  const [method, setMethod] = useState("card");
  const { credits, useCredits } = useRocketCredits(); // ✅ Hook is called at the top level
  const navigate = useNavigate();

  const handlePayment = () => {
    if (method === "rocket") {
      if (credits < 10) {
        alert("Not enough Rocket Credits.");
        return;
      }
      useCredits(10); // ✅ Using the function safely, since it's already initialized at the top level
    }

    alert(`Processing payment with ${method}`);
    navigate("/payment-success");
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold">Select Payment Method</h2>
      <select value={method} onChange={(e) => setMethod(e.target.value)} className="block mt-2 p-2 border">
        <option value="card">Credit/Debit Card</option>
        <option value="paypal">PayPal</option>
        <option value="rocket">Rocket Credits ({credits} available)</option>
      </select>
      <button onClick={handlePayment} className="mt-4 bg-blue-600 text-white px-4 py-2 rounded">Pay Now</button>
    </div>
  );
};

export default Payment;
