import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const Payment = () => {
  const location = useLocation();
  const planDetails = location.state?.planDetails || { name: "Standard Plan", price: 9.99 };

  const [method, setMethod] = useState("card");
  const [showRocketInfo, setShowRocketInfo] = useState(false);
  const [rocketPaymentType, setRocketPaymentType] = useState("credit"); // "credit" or "installment"
  const [returnDate, setReturnDate] = useState("");
  const [returnAmount, setReturnAmount] = useState(0);
  const [installmentPeriod, setInstallmentPeriod] = useState(3); // Default 3 months
  const [cardDetails, setCardDetails] = useState({
    cardNumber: "",
    cardName: "",
    expiryDate: "",
    cvv: ""
  });
  const [paypalDetails, setPaypalDetails] = useState({
    email: "",
    password: ""
  });
  const navigate = useNavigate();

  // Calculate monthly installment amount
  const monthlyInstallment = (planDetails.price / installmentPeriod).toFixed(2);

  const handlePayment = () => {
    if (method === "rocket") {
      if (rocketPaymentType === "credit") {
        // Credit payment validation
        if (!returnDate) {
          alert("Please select a return date.");
          return;
        }
      } else {
        // Installment payment validation
        // No validation needed since we're not checking credit balance
      }
    } else if (method === "card") {
      // Validate card details
      if (!cardDetails.cardNumber || !cardDetails.cardName || !cardDetails.expiryDate || !cardDetails.cvv) {
        alert("Please fill in all card details");
        return;
      }
    } else if (method === "paypal") {
      // Validate PayPal details
      if (!paypalDetails.email || !paypalDetails.password) {
        alert("Please fill in all PayPal details");
        return;
      }
    }

    alert(`Processing payment with ${method}${method === "rocket" ? ` (${rocketPaymentType} plan)` : ""}`);
    navigate("/payment-success");
  };

  const handleRocketSelect = () => {
    setMethod("rocket");
    setShowRocketInfo(true);
  };

  const closeRocketInfo = () => {
    setShowRocketInfo(false);
  };

  const handleCardChange = (e) => {
    const { name, value } = e.target;
    setCardDetails(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePaypalChange = (e) => {
    const { name, value } = e.target;
    setPaypalDetails(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleReturnDateChange = (e) => {
    const selectedDate = e.target.value;
    setReturnDate(selectedDate);

    // Calculate return amount based on selected date
    const today = new Date();
    const returnDay = new Date(selectedDate);
    const diffTime = Math.abs(returnDay - today);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    // Calculate return amount: shorter period = higher return percentage
    let returnPercentage = 0;
    if (diffDays <= 30) {
      returnPercentage = 0.9; // 90% return if within 30 days
    } else if (diffDays <= 60) {
      returnPercentage = 0.7; // 70% return if within 60 days
    } else if (diffDays <= 90) {
      returnPercentage = 0.5; // 50% return if within 90 days
    } else {
      returnPercentage = 0.3; // 30% return if more than 90 days
    }

    setReturnAmount((planDetails.price * returnPercentage).toFixed(2));
  };

  const handleInstallmentPeriodChange = (e) => {
    setInstallmentPeriod(parseInt(e.target.value));
  };

  // Get minimum date (today) and maximum date (1 year from now)
  const today = new Date().toISOString().split('T')[0];
  const maxDate = new Date();
  maxDate.setFullYear(maxDate.getFullYear() + 1);
  const maxDateString = maxDate.toISOString().split('T')[0];

  return (
    <div className="container mx-auto p-4 text-center">
      <h2 className="text-2xl font-bold mb-6">Select Payment Method</h2>
      <div className="bg-gray-800 p-4 rounded-lg mb-6 max-w-md mx-auto">
        <h3 className="text-xl font-semibold mb-2">Selected Plan: {planDetails.name}</h3>
        <p className="text-gray-300">${planDetails.price} / month</p>
      </div>

      <div className="flex justify-center gap-10 mt-4"> {/* Payment images layout */}
        <img 
          src="/images/card.png" 
          alt="Credit Card" 
          className={`payment-icon ${method === "card" ? "selected" : ""}`} 
          onClick={() => setMethod("card")} 
        />
        <img 
          src="/images/paypal.png" 
          alt="PayPal" 
          className={`payment-icon ${method === "paypal" ? "selected" : ""}`} 
          onClick={() => setMethod("paypal")} 
        />
        <img 
          src="/images/rocket.png" 
          alt="Rocket Credits"
          className={`payment-icon ${method === "rocket" ? "selected" : ""}`} 
          onClick={handleRocketSelect} 
        />
      </div>

      {/* Payment Method Forms */}
      <div className="mt-6 max-w-md mx-auto">
        {/* Credit Card Form */}
        {method === "card" && (
          <div className="bg-gray-800 p-6 rounded-lg shadow-lg">
            <h3 className="text-xl font-bold mb-4">Credit Card Details</h3>
            <div className="text-left">
              <div className="mb-3">
                <label className="block text-gray-300 mb-1">Card Number</label>
                <input 
                  type="text" 
                  name="cardNumber"
                  placeholder="1234 5678 9012 3456"
                  value={cardDetails.cardNumber}
                  onChange={handleCardChange}
                  className="w-full"
                />
              </div>
              <div className="mb-3">
                <label className="block text-gray-300 mb-1">Cardholder Name</label>
                <input 
                  type="text" 
                  name="cardName"
                  placeholder="John Doe"
                  value={cardDetails.cardName}
                  onChange={handleCardChange}
                  className="w-full"
                />
              </div>
              <div className="flex gap-4">
                <div className="mb-3 flex-1">
                  <label className="block text-gray-300 mb-1">Expiry Date</label>
                  <input 
                    type="text" 
                    name="expiryDate"
                    placeholder="MM/YY"
                    value={cardDetails.expiryDate}
                    onChange={handleCardChange}
                    className="w-full"
                  />
                </div>
                <div className="mb-3 flex-1">
                  <label className="block text-gray-300 mb-1">CVV</label>
                  <input 
                    type="text" 
                    name="cvv"
                    placeholder="123"
                    value={cardDetails.cvv}
                    onChange={handleCardChange}
                    className="w-full"
                  />
                </div>
              </div>
            </div>
          </div>
        )}

        {/* PayPal Form */}
        {method === "paypal" && (
          <div className="bg-gray-800 p-6 rounded-lg shadow-lg">
            <h3 className="text-xl font-bold mb-4">PayPal Login</h3>
            <div className="text-left">
              <div className="mb-3">
                <label className="block text-gray-300 mb-1">PayPal Email</label>
                <input 
                  type="email" 
                  name="email"
                  placeholder="email@example.com"
                  value={paypalDetails.email}
                  onChange={handlePaypalChange}
                  className="w-full"
                />
              </div>
              <div className="mb-3">
                <label className="block text-gray-300 mb-1">PayPal Password</label>
                <input 
                  type="password" 
                  name="password"
                  placeholder="Your PayPal password"
                  value={paypalDetails.password}
                  onChange={handlePaypalChange}
                  className="w-full"
                />
              </div>
              <p className="text-sm text-gray-400 mt-2">
                You will be redirected to PayPal to complete your payment securely.
              </p>
            </div>
          </div>
        )}

        {/* Rocket Credits */}
        {method === "rocket" && (
          <div className="bg-gray-800 p-6 rounded-lg shadow-lg">
            <h3 className="text-xl font-bold mb-4">Rocket Payment</h3>
            <p className="mb-4">Pay with Rocket - our flexible payment solution.</p>

            {/* Payment Type Selection */}
            <div className="mb-6">
              <h4 className="text-lg font-semibold mb-3">Choose Payment Type</h4>
              <div className="flex justify-center gap-4">
                <button 
                  className={`px-4 py-2 rounded ${rocketPaymentType === "credit" ? "bg-green-600" : "bg-gray-700"}`}
                  onClick={() => setRocketPaymentType("credit")}
                >
                  Credit Plan
                </button>
                <button 
                  className={`px-4 py-2 rounded ${rocketPaymentType === "installment" ? "bg-green-600" : "bg-gray-700"}`}
                  onClick={() => setRocketPaymentType("installment")}
                >
                  Installment Plan
                </button>
              </div>
            </div>

            {/* Credit Plan Options */}
            {rocketPaymentType === "credit" && (
              <div className="border-t border-gray-700 pt-4">
                <h4 className="text-lg font-semibold mb-3">Credit Plan</h4>
                <p className="text-sm text-gray-300 mb-3">
                  Pay the full amount now (${planDetails.price}) and select a return date to be eligible for a partial refund if you decide to cancel.
                </p>

                <div className="text-left mb-4">
                  <label className="block text-gray-300 mb-1">Return Date</label>
                  <input 
                    type="date" 
                    min={today}
                    max={maxDateString}
                    value={returnDate}
                    onChange={handleReturnDateChange}
                    className="w-full"
                  />
                </div>

                {returnDate && (
                  <div className="bg-gray-700 p-3 rounded mb-3">
                    <p className="font-bold">If you cancel by {new Date(returnDate).toLocaleDateString()}:</p>
                    <p className="text-xl text-green-400">You'll receive ${returnAmount} back</p>
                    <p className="text-xs text-gray-400 mt-1">
                      (The return amount decreases the longer you wait to cancel)
                    </p>
                  </div>
                )}
              </div>
            )}

            {/* Installment Plan Options */}
            {rocketPaymentType === "installment" && (
              <div className="border-t border-gray-700 pt-4">
                <h4 className="text-lg font-semibold mb-3">Installment Plan</h4>
                <p className="text-sm text-gray-300 mb-3">
                  Split your payment into monthly installments. The first payment will be charged now.
                </p>

                <div className="text-left mb-4">
                  <label className="block text-gray-300 mb-1">Payment Period (months)</label>
                  <select 
                    value={installmentPeriod} 
                    onChange={handleInstallmentPeriodChange}
                    className="w-full"
                  >
                    <option value="3">3 months</option>
                    <option value="6">6 months</option>
                    <option value="12">12 months</option>
                  </select>
                </div>

                <div className="bg-gray-700 p-3 rounded mb-3">
                  <p className="font-bold">Payment Schedule:</p>
                  <p className="text-md">Total: ${planDetails.price}</p>
                  <p className="text-xl text-green-400">${monthlyInstallment} / month for {installmentPeriod} months</p>
                  <p className="text-xs text-gray-400 mt-1">
                    First payment due today: ${monthlyInstallment}
                  </p>
                </div>

                <p className="text-xs text-gray-400 mt-3">
                  Note: Installment plans require automatic monthly payments from your linked payment method.
                  Make sure your payment method remains valid throughout the installment period.
                </p>
              </div>
            )}
          </div>
        )}
      </div>

      <button onClick={handlePayment} className="mt-6 bg-green-600 max-w-md">Complete Payment</button>

      {/* Rocket Credits Information Modal */}
      {showRocketInfo && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
          <div className="bg-gray-800 p-6 rounded-lg shadow-lg max-w-md w-full">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-bold text-white">About Rocket Payment</h3>
              <button 
                onClick={closeRocketInfo}
                className="text-gray-400 hover:text-white text-2xl"
              >
                &times;
              </button>
            </div>

            <div className="text-left">
              <p className="mb-3">Rocket is our flexible payment solution that gives you more control over how you pay for your subscription.</p>

              <div className="mb-4">
                <h4 className="font-semibold mb-2">Payment Options:</h4>
                <ul className="list-disc pl-5 text-sm">
                  <li className="mb-1"><span className="font-medium">Credit Plan:</span> Pay the full amount now and get a partial refund if you cancel early.</li>
                  <li><span className="font-medium">Installment Plan:</span> Split your payment into monthly installments over 3, 6, or 12 months.</li>
                </ul>
              </div>

              <p className="text-sm text-gray-400">Choose the option that works best for your budget and needs.</p>
            </div>

            <div className="mt-4 flex justify-end">
              <button 
                onClick={closeRocketInfo}
                className="bg-gray-600 text-white px-4 py-2 rounded mr-2"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Payment;
