import React, { createContext, useContext, useState } from "react";

// Create the Context
const RocketCreditsContext = createContext();

// Provider Component
export const RocketCreditsProvider = ({ children }) => {
  const [credits, setCredits] = useState(50); // Default credits

  // Function to use credits for payment
  const deductCredits = (amount) => {
    if (credits >= amount) {
      setCredits((prev) => prev - amount);
      return true;
    }
    return false;
  };

  // Function to add credits (e.g., based on user reputation)
  const addCredits = (amount) => {
    setCredits((prev) => prev + amount);
  };

  return (
    <RocketCreditsContext.Provider value={{ credits, useCredits, addCredits }}>
      {children}
    </RocketCreditsContext.Provider>
  );
};

export const useRocketCredits = () => {
  return useContext(RocketCreditsContext);
};
