import React, { createContext, useContext, useState } from "react";

// Create Auth Context
const AuthContext = createContext();

// Auth Provider Component
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null); // Stores logged-in user

  // Signup Function
  const signup = (email, password) => {
    const existingUser = localStorage.getItem(email);
    if (existingUser) {
      return false; // User already exists
    }
    localStorage.setItem(email, JSON.stringify({ email, password, credits: 50 })); // Store user with Rocket Credits
    setUser({ email, credits: 50 });
    return true;
  };

  // Login Function
  const login = (email, password) => {
    const storedUser = localStorage.getItem(email);
    if (!storedUser) return false; // User not found

    const userData = JSON.parse(storedUser);
    if (userData.password !== password) return false; // Password mismatch

    setUser(userData); // Set user state
    return true;
  };

  // Logout Function (Fixed)
  const logout = () => {
    setUser(null);
    window.location.href = "/login"; // ✅ Safe alternative to navigate()
  };

  return (
    <AuthContext.Provider value={{ user, login, signup, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// Custom Hook to Use Auth Context
export const useAuth = () => {
  return useContext(AuthContext);
};
