import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const success = login(email, password);
      if (success) {
        navigate("/subscribe");
      } else {
        setError("Invalid credentials. Please try again.");
      }
    } catch (err) {
      setError("An error occurred. Please try again.");
    }
  };

  return (
    <div className="container mx-auto p-4 max-w-sm"> {/* Reduced width */}
      <h2 className="text-2xl font-bold text-center">Login</h2>
      {error && <p className="text-red-500">{error}</p>}
      <form onSubmit={handleLogin} className="mt-4">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="w-3/4 p-2 border rounded mt-2 block" /* Reduced width */
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-3/4 p-2 border rounded mt-2 mx-auto block" /* Reduced width */
          required
        />
        <button
          type="submit"
          className="w-3/4 bg-blue-600 text-white px-4 py-2 rounded mt-4 mx-auto block" /* Adjusted width */
        >
          Login
        </button>
      </form>
      <p className="text-center mt-2">
        Don't have an account?{" "}
        <a href="/signup" className="text-blue-600">Sign Up</a>
      </p>
    </div>
  );
};

export default Login;
